mapper.select:
select
id, name, delete, remark, delete_time, create_time, modify_time
from mock_api_info


        and id = #{id}


        and name = #{name}


        and delete = #{delete}


        and remark = #{remark}


        and delete_time = #{deleteTime}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from mock_api_info


        and id = #{id}


        and name = #{name}


        and delete = #{delete}


        and remark = #{remark}


        and delete_time = #{deleteTime}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}
